mapper.select:
SELECT d.*,c.user_id,c.real_name FROM device_info d,auth_user c

    d.user_id = c.user_id
	 AND d.device_id LIKE concat('%',#{device_id,jdbcType=VARCHAR},'%')
	 AND d.device_name LIKE concat('%',#{device_name,jdbcType=VARCHAR},'%')
	 AND c.user_id =#{user_id,jdbcType=VARCHAR}
	 AND c.real_name LIKE concat('%',#{real_name,jdbcType=VARCHAR},'%')
	 AND d.create_time >=#{start_time}
	 AND d.create_time <=#{end_time}

SELECT d.* FROM device_info d

    d.parent_id='0'
	 AND d.user_id =#{_parameter,jdbcType=VARCHAR}
